using namespace std;

struct Node {
    Node* parent;
    map<char, Node*> branch;
    Node(Node* parent) : parent(parent) {}
    bool has(char c) { return branch.end() != branch.find(c); }
};

struct SimpleTrie {
    Node *trie, *cur_node;
    string cur_str;
    SimpleTrie(vector<string>& words) {
        trie = new Node(nullptr);
        for (string s : words) {
            auto ptr = trie;
            for (char c : s) {
                if (!ptr->has(c)) {
                    ptr->branch[c] = new Node(ptr);
                }
                ptr = ptr->branch[c];
            }
            ptr->branch['$'] = nullptr;
        }
        cur_node = trie;
    }
    bool move(char c) {
        // cout << "# " << c << '\n';
        if (!cur_node->has(c)) {
            // cout << "#STOP\n";
            return false;
        }
        cur_node = cur_node->branch[c];
        cur_str.push_back(c);
        return true;
    };
    bool end() { return cur_node->has('$'); };
    string get_string() { return cur_str; };
    void back() {
        cur_node = cur_node->parent;
        cur_str.pop_back();
    };
};

class Solution {
   public:
    void findWords(vector<vector<char>>& board, int i, int j,
                   vector<vector<bool>>& used, SimpleTrie& trie,
                   set<string>& res) {
        used[i][j] = true;
        if (trie.end()) {
            res.insert(trie.get_string());
        }
        int m = board.size(), n = board[0].size();
        vector<pair<int, int>> dirs = {{0, -1}, {0, 1}, {-1, 0}, {1, 0}};
        for (const auto& dir : dirs) {
            int i1 = i + dir.first;
            int j1 = j + dir.second;
            if (0 <= i1 && i1 < m && 0 <= j1 && j1 < n && !used[i1][j1]) {
                if (trie.move(board[i1][j1])) {
                    findWords(board, i1, j1, used, trie, res);
                    trie.back();
                }
            }
        }
        used[i][j] = false;
    }
    vector<string> findWords__TL(vector<vector<char>>& board,
                             vector<string>& words) {
        if (0 == board.size()) {
            return {};
        }
        int m = board.size(), n = board[0].size();
        set<string> res;
        vector<vector<bool>> used(m, vector<bool>(n, false));
        SimpleTrie trie(words);
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (trie.move(board[i][j])) {
                    findWords(board, i, j, used, trie, res);
                    trie.back();
                }
            }
        }

        vector<string> res2(res.begin(), res.end());
        return res2;
    }
};

